import React from 'react'
import "./IndivShoe.css"
import {women, men } from "../../shoeInventory"
import { useParams } from 'react-router-dom'

export const IndivShoe = () => {
    const { name } = useParams();

    // Combine all shoe collections
    const allShoes = [...showcase, ...women, ...men];

    // Find the shoe that matches the name from the URL
    const shoe = allShoes.find(shoe => shoe.name === name);

    if (!shoe) {
        return <div>Shoe not found</div>;
    }

    return (
        <div className="item">
            <div className="image">
                <img src={shoe.image} alt={shoe.name} />
            </div>
            <div className="details">
                <h2>{shoe.name}</h2>
                <p className="price">${shoe.price.toFixed(2)}</p>
                {/* Add more details here as needed */}
            </div>
        </div>
    )
}
