import React from 'react'
import "./IndivShoe.css"
import { showcase, women, men } from "../../shoeInventory"
import { useParams } from 'react-router-dom'

export const IndivShoe = () => {
    const { name } = useParams();
    let allShoes=[...women, ...men]
    return (
        <div className="item">
            <div className="image">
                {allShoes.filter(shoe => shoe.name === name).map((shoe, index) =>(
                  <h2>{shoe.name}</h2>  
                ))}
                <img src={allShoes.image} alt={allShoes.name} />
            </div>
            <div className="details">
                <h2>{allShoes.name}</h2>
                <p className="price">${allShoes.price}</p>
                {/* Add more details here as needed */}
            </div>
        </div>
    )
}
